mapper.select:
SELECT DISTINCT act.id, act.* FROM car_tip AS act, tip_publish_criteria_ex AS c WHERE 1=1 AND act.`status` = 2
AND act.id=c.tip_id and act.publish_channel in (0,1,3) and act.org_code = #{org_code} AND ( (c.m_begin IS NULL) or (c.m_begin='') OR #{current_mileage} >= c.m_begin ) AND ( (c.m_end IS NULL) or (c.m_end='') OR #{current_mileage} <= c.m_end ) AND ( ( (c.car_m_ids IS NULL or c.car_m_ids='' ) AND ( ( (c.car_s_ids IS NULL or c.car_s_ids='') AND ( (c.car_b_ids IS NULL or c.car_b_ids='') OR INSTR(c.car_b_ids, #{brand_id}) >0 ) ) OR ( INSTR(c.car_s_ids, #{serial_id}) >0 ) ) ) OR ( INSTR(c.car_m_ids, #{model_id}) >0 ) ) ORDER BY act.ctime DESC
LIMIT 0,#{limit}
